    for (let y = 10; y < 400; y += 10) {
      stroke(255,0,255);
      curve(noise(x,y+10)*random(200,300), noise(x,y)*random(200,300), x, y, x, y, x, y);
      strokeWeight(5);
    }
  }
}

function setup() {
  createCanvas(400, 400);
}

function draw() {

  background(220);

  //two for loops create the rings of points we can use
  for (var ring = 1; ring < 10; ring++) {
    for (var spikes = 0; spikes < 7 * ring; spikes++) {
      //we take 2PI, divide it by the number of spikes in that ring, then multiply by which spike we are currently on.
      var spikeX = sin(2 * PI / (7 * ring) * spikes); //calculate x position
      spikeX = spikeX * (ring * 40) + 200; //add a radius and move to the middle
      var spikeY = cos(2 * PI / (7 * ring) * spikes); //calculate y position
      spikeY = spikeY * (ring * 40) + 200; //add a radius and move to the middle

      //calculate how long each line is
      var howFar = dist(200, 200, spikeX, spikeY);
      //remap howFar to be good numbers for our strokeWeight
      var thick = map(howFar, 0, 200, 10, 1);

      strokeWeight(thick); //set the line thickness

      line(200, 200, spikeX, spikeY); //draw the line from the center to each point in the rings
    }
  }

}

