Sokoban.prototype.movePlayer1 = function(direction) {
  var v1 = direction.copy().add(this.player1.position);
  var didMove = false;
  var move = {};
  if (!this.walls.getItem(v1)) {

      // if(!this.player2.getItem(v1)){

        var box = this.boxes.getItem(v1);
        if (!box) {
          didMove = true;
        } else {
          var v2 = v1.copy().add(direction);
          if (!this.walls.getItem(v2) && !this.boxes.getItem(v2)) {
            didMove = true;
            box.position.add(direction);
            move.box = box;
            // this.pushes++;
          }
        // }
    }
  }

  if (didMove) {
    move.direction = direction.copy();
    this.undo.push(move);
    this.player1.position = v1;
    // this.moves++;
    if (this.didWin()) {
      // textAlign(CENTER);
      // textSize(48);
      // text('win!',width/2,height,2);
    this.nextLevel();
  }
  }
}
Sokoban.prototype.movePlayer2 = function(direction) {
  var v1 = direction.copy().add(this.player2.position);
  var didMove = false;
  var move = {};
  if (!this.walls.getItem(v1)) {
    var box = this.boxes.getItem(v1);
    if (!box) {
      didMove = true;
    } else {
      var v2 = v1.copy().add(direction);
      if (!this.walls.getItem(v2) && !this.boxes.getItem(v2)) {
        didMove = true;
        box.position.add(direction);
        move.box = box;
        // this.pushes++;
      }
    }
  }

  if (didMove) {
    move.direction = direction.copy();
    this.undo.push(move);
    this.player2.position = v1;
    // this.moves++;
    if (this.didWin()) {
        // textAlign(CENTER);
        // textSize(48);
        // text('win!',100,1002);

      this.nextLevel();
    }
  }
}
Sokoban.prototype.processKey = function(k) {
  var s = String.fromCharCode(k).toLowerCase();
  // Handle player1 moves
  if (s === "w" ) {
    this.movePlayer1(this.UP);
  } else if (s === "a" ) {
    this.movePlayer1(this.LEFT);
  } else if (s === "s" ) {
    this.movePlayer1(this.DOWN);
  } else if (s === "d") {
    this.movePlayer1(this.RIGHT);
  }
  if ( k === UP_ARROW) {
    this.movePlayer2(this.UP);
  } else if ( k === LEFT_ARROW) {
    this.movePlayer2(this.LEFT);
  } else if ( k === DOWN_ARROW) {
    this.movePlayer2(this.DOWN);
  } else if ( k === RIGHT_ARROW) {
    this.movePlayer2(this.RIGHT);
  }
  // Handle undo
  else if (s === "u") {
    this.undoMove();
  }

  // Handle changing levels
  else if (s === "n") {
    this.nextLevel();
  } else if (s === "p") {
    this.previousLevel();
  } else if (s === "r") {
    this.resetLevel();
  }
}
Sokoban.prototype.undoMove = function() {
  if (this.undo.length) {
    var move = this.undo.pop();
    move.direction.mult(-1);
    this.player1.position.add(move.direction);
    if (move.box) {
      move.box.position.add(move.direction);
      this.pushes--;
    }
    this.moves--;
  }
}
Sokoban.prototype.nextLevel = function() {
  this.currentLevel += this.currentLevel < levels.length - 1 ? 1 : 0;
  this.initLevel();
}
Sokoban.prototype.previousLevel = function() {
  this.currentLevel -= this.currentLevel > 0 ? 1 : 0;
  this.initLevel();
}
Sokoban.prototype.resetLevel = function() {
  this.initLevel();
}
Sokoban.prototype.didWin = function() {
  for (var i = 0; i < this.goals.length; i++) {
    var goal = this.goals[i];
    if (!this.boxes.getItem(goal.position)) {
      return false;
    }
  }
  return true;
}

function Walls() {}
Walls.prototype = new DisplayableList;
Walls.prototype.getBoundaries = function() {
  var left = this[0].position.x,
    top = this[0].position.y,
    right = this[0].position.x,
    bottom = this[0].position.y;

  for (var i = 1; i < this.length; i++) {
    var w = this[i];
    if (w.position.x < left) {
      left = w.position.x;
    }
    if (w.position.x > right) {
      right = w.position.x;
    }
    if (w.position.y < top) {
      top = w.position.y;
    }
    if (w.position.y > bottom) {
      bottom = w.position.y;
    }
  }
  return [createVector(left, top), createVector(right, bottom)];
}

function Wall(x, y) {
  this.position = createVector(x, y);
}
