hookdepth = 40;

doordepth = 75;

hooklength1 = 75;

hooklength2 = 200;

hooklength3= 30;

hookwidth = 60;

topthickness = 10;

thickness = 5;

enable_45point   = 0;    //  [ 0:No, 1:Yes ]

enable_fillet  = 0;     // [ 0:No, 1:Yes ]

enable_curl   = 0;    



adjustment= ( enable_curl == 1 ) ? thickness+hookdepth/2 :0;


rotate([0,270,270]){    
    union(){

        translate([0,thickness+hookdepth,adjustment])
        cube([hookwidth,thickness,hooklength2-adjustment]);  


        translate([0,2*thickness+hookdepth,hooklength2-topthickness])
        cube([hookwidth,doordepth,topthickness]);   


        translate([0,2*thickness+doordepth+hookdepth,hooklength2-hooklength3])
        cube([hookwidth,thickness,hooklength3]);

        if ( enable_45point ){
                if ( enable_fillet ){
                                    r=min(hooklength1-(adjustment+thickness),hookwidth/2);
                                    echo(r);
                                    echo(hookwidth/2);
                                    translate([0,0,adjustment]) cube ( [ hookwidth, thickness, (hooklength1)-(r+adjustment)] );
                                    intersection(){
                                                    union(){
                                                            hull(){
                                                                        translate([0,0,hooklength1-r])
                                                                            rotate([-90,270,0])
                                                                            linear_extrude(height = thickness, center = false, convexity = 10, twist = 0)
                                                                            polygon(points=[[0,0],[r,hookwidth/2],[0,hookwidth]]);
                                                                        translate([hookwidth/2,thickness,hooklength1-minifillet])
                                                                            rotate([90,0,0])
                                                                            cylinder(r=minifillet,h=thickness, center=false,$fn=128);
                                                            }
                                                        }
                                                        translate ( [  0, 0, adjustment ] ) cube([hookwidth,thickness,hooklength1-adjustment]);   
                                    }
                }
                else {
                                    r=min(hooklength1-(adjustment+thickness),hookwidth/2);
                                    translate([0,0,adjustment]) cube ( [ hookwidth, thickness, (hooklength1)-(r+adjustment)] );
                                    translate([0,0,hooklength1-r])
                                    rotate([-90,270,0])
                                    linear_extrude(height = thickness, center = false, convexity = 10, twist = 0)
                                    polygon(points=[[0,0],[r,hookwidth/2],[0,hookwidth]]);
                }
        }
        else {
                if ( enable_fillet ){

                    r=min((hooklength1-adjustment)*2,hookwidth/2)/2;
                    echo (r,adjustment,hooklength1);
                    translate([0,0,adjustment]) cube ( [ hookwidth, thickness, hooklength1-r-adjustment] );

                    intersection(){
                        union(){
                                        rotate([90,0,0])translate ( [  r, hooklength1-r, -thickness] ) cylinder ( r = r, h = thickness,$fn=128);
                                        rotate([90,0,0])translate ( [  hookwidth - r, hooklength1-r, -thickness] ) cylinder ( r = r, h = thickness,$fn=128 );
                                        translate ( [  r, 0, hooklength1-2*r ] ) cube ( [ hookwidth-2*r, thickness, 2*r] );
                        }
                        translate ( [  0, 0, adjustment ] ) cube([hookwidth,thickness,hooklength1-adjustment]);
                    }
                }
                else {
                        translate([0,0,adjustment])
                        cube([hookwidth,thickness,hooklength1-adjustment]);
                }
        }  

       if (enable_curl){

            intersection(){
                translate([0,hookdepth/2+thickness,hookdepth/2+thickness])
                rotate([0,90,0])   
                rotate_extrude($fn=128)
                translate([hookdepth/2,0,0])
                square([thickness,hookwidth]);    

            cube([hookwidth,2*thickness+hookdepth,hookdepth/2+thickness]);
            }

        }
        else {

            translate([0,thickness,0])
            cube([hookwidth,hookdepth,thickness]);
        }
    }
}

