function setup() {
  createCanvas(500, 300);

  createP('Amplitude');
  amplitude_slider = createSlider(0, 100, 50);

  createP('Frequency');
  frequency_slider = createSlider(0, 20, 5);

  createP('Time');
  time_slider = createSlider(0, 1000, 500);
}


function draw() {
  background(50);
  ellipseMode(CENTER);

  var amplitude = amplitude_slider.value() / 100;
  var frequency = frequency_slider.value();
  var time = 1001 - time_slider.value();

  noiseDetail(1, 0.5);

  fill(255);
  noStroke();

  // study this loop. do you understand how the line of ellipses is created?
  for (i = 0; i < 1; i += 0.02) {
    var x = lerp(startX, endX, i);
    var y = lerp(startY, endY, i);

    // hint: drive offsetX and offsetY with noise() instead of random()
    var offsetX = noise(i * frequency, time, millis() / 1000) * amplitude * 100;
    var offsetY = noise(i * frequency, time, millis() / 1000) * amplitude * 100;

    ellipse(x + offsetX, y + offsetY, 10, 10);
  }
}

