// if point is within a minimum distance of the mouse - move point away until outside of the zone - if mouse to the left, move point to the right, if mouse to the right, move point to the left ,etc. 

let points; 
var POINT_COUNT = 100;

function setup() {
  createCanvas(400, 400);
  colorMode(HSB, 100);
}

function draw() {
    // clear the background
    background(15, 7, 96);

    noStroke();
      points = placePointsCullRange(POINT_COUNT, width, height, 10, width)
    mouseMoved(points);
}



function drawPoints(points) {
    // loop through the points
    for (var i = 0; i < points.length; i++) {
        // pull the coordianates from the current point in array
        var x = points[i].x;
        var y = points[i].y;

        let s = map(noise(i, POINT_COUNT), 0, 1, 0, 100);
        fill(70, s, 80, 90, .5)
        diameter = map(noise(i, POINT_COUNT), 0, 1, 2, 10);
        rect(x, y, diameter, diameter);
    }
}

function placePointsCullRange(count, w, h, min_distance, max_distance) {

    var points = [];

    var current_try = 0;
    var max_tries = count * 10;

    // init with one point in center
    points.push({
        x: w * .5,
        y: h * .5
    });

    while (points.length < count && current_try < max_tries) {
        current_try++;

        var x = map(noise(w + count, current_try), 0, 1, 0, w);
        var y = map(noise(h + count, current_try/2), 0, 1, -0, h);

        var isTooClose = checkClose(x, y, points, min_distance);
        var isCloseEnough = checkClose(x, y, points, max_distance);

        if (!isTooClose && isCloseEnough) {
            points.push({
                x: x,
                y: y,
            });
        }
    }

    return points;
}

function checkClose(x, y, points, min_distance) {

    for (var i = 0; i < points.length; i++) {
        if (dist(points[i].x, points[i].y, x, y) < min_distance) {
            return true;
        }
    }
    return false;
}

function mouseMoved(points){
  for (var i = 0; i < points.length; i++){
    let x1 = points[i].x;
    let y1 = points[i].y;
    let x2 = mouseX;
    let y2 = mouseY;

    if (dist(x1, y1, x2, y2) < 20){
      console.log('close');
      // if the point is to the left of the mouse
      if (x1 < x2){
        x1 -= 20 - dist(x1, 0, x2, 0);
      // if the point is to the right of the mouse
      } else if (x2 < x1){
        x1 += 20 - dist(x1, 0, x2, 0);
      }
      // if the point is above the mouse
      if (y1 < y2){
        y1 -= 20 - dist(0, y1, 0, y2)
      } else if (y2 < y1){
        y1 += 20 - dist(0, y1, 0, y2);
      }
    points.splice(i, 1,({
      x: x1,
      y: y1
    }))

    }

  }
  drawPoints(points);
  return false;
}

