let lfo = new LFO(1, 10, 1);

function LFO(min, max, step = 1) {
  const damp = .01;
  this.max = max;
  this.min = min;
  this.step = step;
  this.run = () => {
    return (
      this.min + this.max * Math.sin(radians(Math.PI * frameCount * (this.step * damp)))
    );
  };
}

function drawShape() {
  let n = lfo.run();
  let g = 1.61803;
  for (let i = 1; i < 2000; i += 1) {
    let c = color(n + i % 255);
    push();
    translate(centerX, centerY);
    rotate(i * g + n);
    stroke(c);
    fill(c);
    scale(n);
    bezier(i, i, n, n, i, i * n, i * 100 * 20, 0);
    pop();
  }
}

