const dim = 800;
var h = 0,
  w = 0;

var recording = false;
var recorder;
var chunks = [];

const fr = 60;

function setup() {
  createCanvas(dim, dim);
  background(0);
  colorMode(HSB);

  frameRate(fr);

  // initalize recorder
  record();
}

function draw() {
  drawLine();
}

function drawLine() {
  translate(dim / 2, dim / 2);
  rotate(frameCount / 500);

  h += 0.05
  w += 0.005

  let x = map(noise(w), 0, 1, 0, dim / 2);
  let y = map(noise(h), 0, 1, 0, dim / 2);

  stroke((frameCount / 2) % 360, 255, 127, 64);

  line(x, y, -1 * x, -1 * y);
}

function mousePressed() { // RESET
  clear();
  background(0);
  h = 0;
  w = 0;
}

function record() {
  chunks.length = 0;

  let stream = document.querySelector('canvas').captureStream(fr);

  recorder = new MediaRecorder(stream);

  recorder.ondataavailable = e => {
    if (e.data.size) {
      chunks.push(e.data);
    }
  };

  recorder.onstop = exportVideo;
}

function exportVideo(e) {
  var blob = new Blob(chunks, {
    'type': 'video/mp4'
  });

  // Draw video to screen
  var videoElement = document.createElement('video');
  videoElement.setAttribute("id", Date.now());
  videoElement.controls = true;
  document.body.appendChild(videoElement);
  videoElement.src = window.URL.createObjectURL(blob);

  // Download the video 
  var url = URL.createObjectURL(blob);
  var a = document.createElement('a');
  document.body.appendChild(a);
  a.style = 'display: none';
  a.href = url;
  a.download = 'hsbStrokes.mp4';
  a.click();
  window.URL.revokeObjectURL(url);
}

function keyPressed() {
  recording = !recording

  if (keyCode === 32 && recording) { // SPACEBAR
    console.log("recording started!");
    recorder.start();
  }

  // if we are recording, stop recording 
  if (keyCode === 32 && !recording) { // SPACEBAR
    console.log("recording stopped!");
    recorder.stop();
  }

  if (keyCode === 83 && recording) { // Letter S
    saveCanvas('hsbStrokes', 'jpg');
    recording = !recording;
  }

}

