var myTurtle;
var numButterflies = 20;

function setup() {
	createCanvas(500, 500);
	noFill();
	background(125, 125, 150);
	noLoop();
	myTurtle = new Turtle();
}



function draw() {
	myTurtle.penUp();

	for (let j = 0; j < numButterflies; j++){
	  drawButterfly(floor(random(50, 450)), floor(random(50, 450)), random(10, 100))  
	}

}


function drawButterfly(x, y, length) {

    myTurtle.moveTo(x,y);
    stroke(random(200, 255), random(200, 255), random(200, 255));
    strokeWeight(random(1, 2));
    myTurtle.turnLeft(90);
    myTurtle.penDown();
    myTurtle.moveForward(length);

    // draw antenna

    myTurtle.pushState();
    myTurtle.turnRight(45);
    myTurtle.moveForward(length/3);
    myTurtle.popState();

    myTurtle.pushState();
    myTurtle.turnLeft(45);
    myTurtle.moveForward(length/3);
    myTurtle.popState();

    // move down the spine of the butterfly for the wings

    myTurtle.penUp();
    myTurtle.moveTo(x, y - length + (length/15));
    myTurtle.penDown();

    // draw left wing
    myTurtle.pushState();
    myTurtle.turnLeft(30);
    for (let t = 0; t < 30; t++){
        myTurtle.moveForward(length/17);
        myTurtle.turnLeft(180/20);
    }
    myTurtle.turnRight(2000);
    for (let k = 0; k < 23; k++){
        myTurtle.moveForward(length/25);
        myTurtle.turnLeft(180/20);
    }
    myTurtle.popState();

    // draw right wing
    myTurtle.popState();
    myTurtle.turnRight(30);
    for (let t = 0; t < 30; t++){
        myTurtle.moveForward(length/17);
        myTurtle.turnRight(180/20);
    }
    myTurtle.turnLeft(2000);
    for (let k = 0; k < 23; k++){
        myTurtle.moveForward(length/25);
        myTurtle.turnRight(180/20);
    }
    myTurtle.popState();

    myTurtle.penUp();
    myTurtle.turnTo(0);

}

