function valueFromDeck() {
  // find the value at the current position in the deck
  var v = values[position];

  // change the position for next time
  position++;

  // if we run out of "cards", shuffle and start over from the top
  if (position == values.length) {
    values = shuffle(values);
    position = 0;
  }

  // return the value
  return v;
}

function valueFromBlue() {
  // find the value at the current position in the deck
  var v = blueHue[position1];

  // change the position for next time
  position1++;

  // if we run out of "cards", shuffle and start over from the top
  if (position1 == blueHue.length) {
    blueHue = shuffle(blueHue);
    position1 = 0;
  }

  // return the value
  return v;
}

///  https://p5js.org/examples/math-noise-wave.html
function waves() {
  push();

  fill(30, 15, 180);

  beginShape();

  let xoff = 500;

  // Iterate over horizontal pixels
  for (let x = 0; x <= width; x += 10) {
    // Calculate a y value according to noise, map to
    let y = map(noise(xoff, yoff), 0, 1, 300, 900);

    // Set the vertex
    vertex(x, y);
    // Increment x dimension for noise
    xoff += 0.05;
  }

  // increment y dimension for noise
  yoff += 0.01;
  vertex(width, height / 2 + 100);
  vertex(0, height / 2 + 100);
  endShape(CLOSE);
  pop();
}

