class mainCharacter {

  constructor(x, y, w, h) {
    this.x = x;
    this.y = y;
    this.w = w;
    this.h = h;
  }
  kevin(mainchar) { 
    imageMode(CENTER);
    image(mainchar, this.x, this.y, this.w, this.h);
  }


  move() { //kevin moves
    if (keyIsDown(38)) {
      this.y -= 3;
    }
    if (keyIsDown(40)) {
      this.y += 3;
    }
    if (keyIsDown(37)) {
      this.x -= 3;
    }
    if (keyIsDown(39)) {
      this.x += 3;
    }

  }

  eat(pos) { //kevin eats

    let xx = this.x;
    let y = this.y;
    if (abs(xx - pos.x) <= 30 && abs(y - pos.y) <= 30) { 
      return true;
    }
    return false;
  }
}

class sceneObject { 
  constructor(x, y, w, h, img) {
    this.x = x;
    this.y = y;
    this.w = w;
    this.h = h;
    this.img = img;
  }

  body() {
    image(this.img, this.x, this.y, this.w, this.h);
  }
}

