var POINT_COUNT = 1500;
var NUM_LAYERS = 90;

function setup() {
  createCanvas(400, 400);
  colorMode(HSB, 100);
}

function draw() {
    // clear the background
    background(15, 7, 96);

    noStroke();
    for (let k = 0; k < NUM_LAYERS; k++){
      points = placePointsCullRange(POINT_COUNT, width, height, 30, width-10, k)
    drawPoints(points, k);
}
  noLoop();


}

function drawPoints(points, k) {
    // loop through the points
    for (var i = 0; i < points.length; i++) {
        // pull the coordianates from the current point in array
        var x = points[i].x;
        var y = points[i].y;

        // draw an ellipse at point
        // noStroke();
        // fill(255);
        let s = map(noise(i, POINT_COUNT), 0, 1, 0, 100);
        let h = 70 - map(noise(k, POINT_COUNT), 0, 1, 5, 20)
        fill(h, s, 80, 90, .5)
        diameter = map(noise(i, POINT_COUNT), 0, 1, 2, 10);
        rect(x, y, diameter, diameter);
    }
}

function placePointsCullRange(count, w, h, min_distance, max_distance, factor) {

    var points = [];

    var current_try = 0;
    var max_tries = count * 10;

    // init with one point in center
    points.push({
        x: w * .5,
        y: h * .5
    });

    while (points.length < count && current_try < max_tries) {
        current_try++;

        var x = map(noise(w + count * factor, current_try), 0, 1, -w/2, w*2);
        var y = map(noise(h + count * factor, current_try/2), 0, 1, -w/2, h*2);

        var isTooClose = checkClose(x, y, points, min_distance);
        var isCloseEnough = checkClose(x, y, points, max_distance);

        if (!isTooClose && isCloseEnough) {
            points.push({
                x: x,
                y: y,
            });
        }
    }

    return points;
}

function checkClose(x, y, points, min_distance) {

    for (var i = 0; i < points.length; i++) {
        if (dist(points[i].x, points[i].y, x, y) < min_distance) {
            return true;
        }
    }
    return false;
}

