// require https://cdn.jsdelivr.net/npm/p5@1.2.0/lib/p5.js

const dim = 800;
const gridSize = 10;

var colorPallete = [ '#152A3B', '#158ca7', '#F5C03E', '#D63826', '#F5F5EB' ];
var position = 0;

function setup() {
  createCanvas(dim, dim);
  noStroke();

  shuffle(colorPallete, true);

  generateCanvas();
}

function draw() {

}

function generateCanvas() {

  clear();

  let blockSize = int(dim / gridSize);

  for (let i = 0; i < dim; i += blockSize) {
    for (let j = 0; j < dim; j += blockSize) {
      shuffle(colorPallete,true);
      let rectColor = colorPallete[0];
      let semiCircle1 = colorPallete[1];
      let semiCircle2 = colorPallete[2];

      switch (int(random(0, 2))) {
        case 0: // VERTICAL POSITIONED
          fill(rectColor);
          rect(i, j, blockSize, blockSize);
          fill(semiCircle1);
          arc(i + blockSize / 2, j, blockSize, blockSize, 0, PI);
          fill(semiCircle2);
          arc(i + blockSize / 2, j + blockSize, blockSize, blockSize, -PI, 0);

          fill('black');
          strokeWeight(2);
          point(i + blockSize / 2, j + blockSize / 2);
          break;

        case 1: // HORIZONTAL POSITIONED
          fill(rectColor);
          rect(i, j, blockSize, blockSize);
          fill(semiCircle1);
          arc(i, j + blockSize / 2, blockSize, blockSize, -HALF_PI, HALF_PI);
          fill(semiCircle2);
          arc(i + blockSize, j + blockSize / 2, blockSize, blockSize, HALF_PI, -HALF_PI);
          break;
      }
    }
  }
}

function mousePressed() {
  generateCanvas();
}

function keyPressed() {
  if (keyCode === 32) { // SPACE KEY
    saveCanvas("W2S4_" + int(millis()), 'jpg');
  }
}

