  sliderspeed = createSlider(0, 1, .01, .02);


}


function draw() {
  background(80, 150);
  ellipseMode(CENTER);

  let amplitude = slideramplitude.value();
  let frequency = sliderfrequency.value();
  let speed = sliderspeed.value();


  noiseDetail(1.2, .25);

  blendMode(HARD_LIGHT);


  for (i = 0; i < 100; i++) {

    stroke(255, (noise(i * frequency + s))  * 255);
    strokeWeight(3);
    let x = lerp(startX, endX, i / 100 * 1.11);
    let y = lerp(startY, endY, i / 100 * 1.11);


    let offsetX = (noise(i * frequency + s)) * amplitude * 10;
    let offsetY = (noise(i * frequency + s)) * amplitude * 10;

    x1 = x - offsetX;
    x2 = x;
    y1 = y;
    y2 = y - offsetY;

    line(x1, y1, x2, y2);
  }

  s += speed;

}

