}

function setup(){
  createCanvas(img.width, img.height);
}

function draw(){
  background(0);

  for (var y = 0; y < img.height; y++){
    for (var x = 0; x < img.width; x++){
      var in_color = getQuick(img,x,y);
      var diag_color = getQuick(img,x+20, y+20);

      var out_color;
      out_color = [
          cos(diag_color[0]) * 255,
          in_color[1],
          diag_color[2],
          in_color[3],
        ]

      img.set(x,y, out_color);

    }
  }
  img.updatePixels();
  noSmooth();
  image(img, 0, 0, width, height);

  noLoop();
}

function getQuick(img, x, y) {
  var i = (y * img.width + x) * 4;
  return [
    img.pixels[i],
    img.pixels[i + 1],
    img.pixels[i + 2],
    img.pixels[i + 3]
  ];
}

