function drawBranch(length,left,right,stroke) {

    if (length < 10) {
        return;
    }

    // draw this branch
    myTurtle.moveForward(length + 5);

    // left child
    myTurtle.pushState();
    myTurtle.turnLeft(left);
    drawBranch(length * 0.75,10,10,random(.1,3));
    // drawPetalLeft();

    //drawBranch(length * random(.5, .9));

    myTurtle.popState();

    // right child
    myTurtle.pushState();
    myTurtle.turnRight(right);
    drawBranch(length * 0.75,10,10,random(.1,3));
    // drawPetalRight();
    //drawBranch(length * random(.5, .9));
    myTurtle.popState();

        // third child
    myTurtle.pushState();
    strokeWeight(stroke);
    myTurtle.turnRight(right/100);
    drawBranch(length * 0.75,10,10,random(.1,3));
    // drawPetalRight();
    //drawBranch(length * random(.5, .9));
    myTurtle.popState();


}

