    var myTurtle;

    function setup() {
      createCanvas(windowWidth, windowHeight);
      noFill();
      stroke(0);
      background(255);
      noLoop();
      myTurtle = new Turtle();
    }



    function draw() {
      myTurtle.penUp();
      myTurtle.moveTo(windowWidth / 2, windowHeight);
      myTurtle.turnTo(-80);

      myTurtle.penDown();

      drawBranch(80);

      for (var i = 0; i < 8; i++) {
        myTurtle.moveTo(windowWidth / 2 - i, windowHeight / 3 - i);
        for (var j = 0; j < 14; j++) {
          myTurtle.moveForward(j * 28);
          drawBlossom(20);
        }
      }
    }



    function drawBlossom(cherry) {
      if (cherry < 4) {
        return;
      }

      // draw cherry blossom
      strokeWeight(cherry / 12);
      stroke(random(219,255), random(0,228), random(100,255));
      myTurtle.moveForward(cherry);
      myTurtle.turnLeft(random(10, 100));
      myTurtle.moveForward(cherry);
      myTurtle.turnLeft(random(10, 100));
      myTurtle.moveForward(cherry);

      // draw child
      myTurtle.pushState();
      myTurtle.turnTo(random(-200, 200));
      drawBlossom(cherry * .8);
      myTurtle.turnTo(random(-200, 200));
      drawBlossom(cherry * .8);
      myTurtle.popState();

    }



    function drawBranch(length) {

      if (length < 4) {
        return;
      }

      // draw this branch
      strokeWeight(length / 4);
      stroke('#53350A');
      myTurtle.moveForward(length);
      myTurtle.turnLeft(28);
      myTurtle.moveForward(length);
      myTurtle.turnLeft(-8);
      myTurtle.moveForward(length);

      // left child
      myTurtle.pushState();
      myTurtle.turnLeft(random(4, 8));
      drawBranch(length * random(.4, .8));
      myTurtle.popState();

      // right child
      myTurtle.pushState();
      myTurtle.turnRight(random(40, 60));
      drawBranch(length * random(.4, .8));
      myTurtle.popState();

    }

