  // Based on 2D Noise example from p5.org/Examples. Source code: https://p5js.org/examples/math-noise2d.html

let canvasSize = 900;
let noiseVal;
let noiseScale = .01;
let harmNum = 4;
let fallOff = 0.6;
let oceanColor = [14, 46, 102];
let colorShift = .85;
let colorScale = .99;
let landColor = [133, 255, 199];



function setup() {
  createCanvas(canvasSize, canvasSize);
   // noiceDetail of the pixels octave count and falloff value
  noiseDetail(harmNum, fallOff);
}

function draw() {
  background(0);

  // Draw noise map
  for (let y = 0; y < height; y++) {
    for (let x = 0; x < width; x++) {
      noiseVal = noise(x * noiseScale, y * noiseScale);
      color1 = ((1-noiseVal) * oceanColor[0] + noiseVal * landColor[0]) * colorScale
      color2 = ((1-noiseVal) * oceanColor[1] + noiseVal * landColor[1]) * colorScale
      color3 = ((1-noiseVal) * oceanColor[2] + noiseVal * landColor[2]) * colorScale

      colorAverage = (color1 + color2 + color3) / 3;

      if (colorAverage > (255 / 2)) {

        color1 = color1 * colorShift

        if (color1 > 255) (
          color1 = 255
        )

        color2  = color2 * colorShift

        if (color2 > 255) (
          color2 = 255
        )
        color3  = color3 * colorShift

        if (color3 > 255) (
          color3 = 255
        )
      }

        else {

        color1 = color1 / colorShift

        color2  = color2 / colorShift

        color3  = color3 / colorShift

        }

      stroke (color1, color2, color3);
      point (x, y);
    }
    }
}

