function mySketch (loopNum){
// draw background color

//draw first horizontal line
x1 = random(0,canvasSize);
y1 = random(0,canvasSize);
x2 = random(0,canvasSize);
y2 = y1;
stroke(255);
strokeWeight(ls);
line(x1,y2,x2,y2);
circWidth = random(10,50);
lineDr = 1;

//create loop 
while (loopNum != 0) {
endPoint = random(0, canvasSize)
//draw line and circle starting at the point x2,y2
drawLinecircle (x2, y2, circWidth, endPoint, lineDr);
if (lineDr) { 
  lineDr = 0;
  y2 = endPoint;
  circWidth = random(10,50);
}
else {
  lineDr = 1;
  x2 = endPoint;
   circWidth = circWidth * 2;
}

loopNum = loopNum -1;
}
}

function drawLinecircle (startX, startY, circleWidth, endXY, dr) {
  //telling function what task to do, draw the second line with circle at starting point
  stroke(255);
  strokeWeight(ls);

  if (dr) {
    line (startX,startY,startX,endXY);
  }
  else {
    line (startX,startY,endXY,startY);

  }
 fill (random(0,255),random(0,255),random(0,255),random(125,255));
 ellipse (startX,startY,circleWidth, circleWidth);
}

