let ms = [];
let bs = [];
let b = [];
let w = 800;
let h = 800;
const V = ["golum", "vader", "hannibal", "sarumon"];
const bF = ["mordor", "deathstar", "silence"];
function preload() {
  V.forEach((e) => ts.push(loadImage("/images/" + e + "Top.png")));
  V.forEach((e) => ms.push(loadImage("/images/" + e + "Mid.png")));
  V.forEach((e) => bs.push(loadImage("/images/" + e + "Bot.png")));
  bF.forEach((e) => b.push(loadImage("/images/" + e + ".jpg")));
}
function setup() {
  createCanvas(w, h);
  x = w / 2;
  y = h / 2;
  noStroke();
  createP("Top");
  tS = createSlider(0, 3, 1);
  createP("Mid");
  mS = createSlider(0, 3, 1);
  createP("Bot");
  bS = createSlider(0, 3, 1);
  bgS = createSlider(0, 2, 1);
  bgS.position(10, h - 50);
}
function draw() {
  tSv = tS.value();
  mSv = mS.value();
  bSv = bS.value();
  dBg();
  push();
  fill(255);
  textSize(20);
  text("background", 10, h - 60);
  pop();
  translate(-70, -70);
  dT();
  dM();
  dB();
}
function dBg() {
  image(b[bgS.value()], 0, 0, w, h);
}
function dT() {
  if (tSv === 0) {
    image(ts[tSv], x, y + 35);
  }
  if (tSv == 1) {
    image(ts[tSv], x, y);
  }
  if (tSv == 2) {
    image(ts[tSv], x, y + 30);
  }
  if (tSv == 3) {
    push();
    scale(1.3);
    image(ts[tSv], x - 80, y - 55);
    pop();
  }
}
function dM() {
  if (mSv === 0) {
    image(ms[mSv], x, y + 137);
  }
  if (mSv == 1) {
    image(ms[mSv], x - 8, y + 55);
  }
  if (mSv == 2) {
    image(ms[mSv], x + 5, y + 135);
  }
  if (mSv == 3) {
    push();
    scale(1.3);
    image(ms[mSv], x - 90, y + 12);
    pop();
  }
}

function dB() {
  if (bSv === 0) {
    image(bs[bSv], x + 12, y + 168);
  }
  if (bSv == 1) {
    image(bs[bSv], x + 27, y + 168);
  }
  if (bSv == 2) {
    image(bs[bSv], x + 21, y + 168);
  }
  if (bSv == 3) {
    image(bs[bSv], x - 10, y + 164);
  }
}

