var gridSize = 5;
var data = [];

const dim = 800;

function setup() {
  createCanvas(dim, dim);
  init();
}

function init() {

  // initialize 2D array
  data = [];
  for (let i = 0; i < gridSize; i++) {
    data[i] = [];
    for (let j = 0; j < gridSize; j++) {
      data[i][j] = false;
    }
  }

  // Assign random blocks
  let lights = int(random(1, 5));
  while (lights) {
    let x = int(random(gridSize));
    let y = int(random(gridSize));

    if (!data[x][y]) {
      data[x][y] = true;
      lights--;
    }
  }

  // Draw Blocks
  drawLayout();

}

function drawLayout() {
  strokeWeight(4);
  stroke('white');

  for (let i = 0; i < gridSize; i++) {
    for (let j = 0; j < gridSize; j++) {

      if (data[i][j])
        fill(251, 49, 38);
      else
        fill(21, 81, 180);

      let blockSize = dim / gridSize;
      rect(blockSize * i, blockSize * j, blockSize, blockSize);
    }
  }
}

function mousePressed() {
  let i = int(mouseX / width * gridSize);
  let j = int(mouseY / height * gridSize);

  toggle(i, j);
  toggle(i-1, j);
  toggle(i+1, j);
  toggle(i, j-1);
  toggle(i, j+1);

}

function toggle(i, j) {
  if ((i >= 0 && i < gridSize) && (j >= 0 && j < gridSize))
    data[i][j] = !data[i][j];
}

function draw() {
  drawLayout();
}

