}

function getC(img, x, y) {
  // wrap
  x = floor(x) % img.width;
  if (x < 0) x += img.width;
  y = floor(y) % img.height;
  if (y < 0) y += img.width;

  // clamp
  //   x = constrain(floor(x), 0, img.width);
  //   y = constrain(floor(y), 0, img.height);

  const i = (y * img.width + x) * 4;
  return [
    img.pixels[i + 0],
    img.pixels[i + 1],
    img.pixels[i + 2],
    img.pixels[i + 3],
  ];
}

function setC(img, x, y, c) {
  // wrap
  x = floor(x) % img.width;
  if (x < 0) x += img.width;
  y = floor(y) % img.height;
  if (y < 0) y += img.width;

  // clamp
  //   x = constrain(floor(x), 0, img.width);
  //   y = constrain(floor(y), 0, img.height);

  const i = (y * img.width + x) * 4;
  img.pixels[i + 0] = c[0];
  img.pixels[i + 1] = c[1];
  img.pixels[i + 2] = c[2];
  img.pixels[i + 3] = c[3];
}

