    for (let y = top; y < top + 100; y += 10) {
      line(x, y, x + 100, y + 100);
      strokeWeight(2);
      stroke(0);
    }
  }
}
function path(left, top, markLeft, markTop) {
  for (let x = left; x < left + 100; x += 10) {
