// require https://cdn.jsdelivr.net/npm/p5@1.2.0/lib/p5.js

const dim = 800;
const size = 15;
const margin = 30;

var colorPallete = ['#f3e8ee', '#475B63', '#57CC99', '#80ED99', '#C7F9CC'];
var position = 0;

var upColor;
var downColor;

function setup() {
  createCanvas(dim, dim);

  shuffle(colorPallete, true);
  bgColor = colorFromDeck();
  upColor = colorFromDeck();
  downColor = colorFromDeck();

  noStroke();

  background(bgColor);
  generateCanvas();
}

function draw() {

}

function generateCanvas() {
  translate(margin, margin);

  let blockSizeX = (dim - 2*margin) / size;
  let blockSizeY = (dim - 2*margin) / (2*size);

  for (let i = 0; i < size; i++) {
    for (let j = 0; j < size*2; j++) {
      let x = i * blockSizeX;
      let y = j * blockSizeY;
      switch (floor(random(0, 2))) {
        case 0: // up triangle
          fill(upColor);
          triangle(
            x + blockSizeX / 2, y,
            x, y + blockSizeY,
            x + blockSizeX, y + blockSizeY
          );
          break;
        case 1: // down triangle
          fill(downColor);
          triangle(
            x, y,
            x + blockSizeX, y,
            x + blockSizeX / 2, y + blockSizeY
          );
          break;
      }
    }
  }
}

