function drawFur(x, y) {

	strokeWeight(1);
	colorMode(HSB)
    stroke(random(20, 60), random(5, 100), random(20, 60), random(10, 100));

    var furHeight = min(
        random(1, 10), random(1, 10), random(1, 20)
    );

    var furLean = random(-1, 0.5);
    furLean *= furHeight;

    line(x, y - furHeight, x + furLean, y);

}

function getQuick(img, x, y) {

    var i = (y * img.width + x) * 4;
    return [
        img.pixels[i],
        img.pixels[i + 1],
        img.pixels[i + 2],
        img.pixels[i + 3],
    ];
}

