let i = 0;
let width1;
let height1;

let topBucket = 0;
let bottomBucket = 0;
let leftBucket = 0;
let rightBucket = 0;
let rectWidth;

let r;
let g;
let b;
let rr;
let gg;
let bb;
let aa;

function setup() {
  randomSeed(12);
  width1 = windowWidth / 2;
  height1 = windowHeight / 2;
  createCanvas(width1, height1);
  frameRate(10);
  rectWidth = width / 4;

  background(15, 15, 15, 255);
}

function draw() {
    rect(0,height-45,width1,45)
  i++;
  if (i > height1 / 9-2) {
    i = 0;
    resetBuckets();

    //clear()
    background(15, 15, 15, 255);
    fill(255)

  }

  x1 = i * 5;
  x2 = width1 - i * 5;
  y1 = 5 * i;
  y2 = 5 * i;

  r = map(x1, 0, max(x1), 0, 255);
  g = map(x2, 0, max(x2), 0, 255);
  b = map(y1, max(y1), 0, 0, 255);
  a = map(y2, 0, 500, 0, 255);

  rando();
  displayResults();
  persistentRects();

} // end draw

function rando() {
  strokeWeight(3);
  let pickLine = floor(random(0, 4));

  if (pickLine == 0) {
    topLine();
    topBucket++;
  }
  if (pickLine == 1) {
    bottomLine();
    bottomBucket++;
  }
  if (pickLine == 2) {
    rightLine();
    rightBucket++;
  }
  if (pickLine == 3) {
    leftLine();
    leftBucket++;
  }
  if (topBucket > bottomBucket) {
    stroke(rr, 0, 0, 255);
  }
  if (bottomBucket > topBucket) {
    stroke(0, 0, bb, 255);
  }
  if (rightBucket - 1 > leftBucket) {
    stroke(0, gg, 0, 255);
  }
  if (leftBucket - 1 > rightBucket) {
    stroke(aa, 255);
    fill(0, 0, 0, 255);
  }
}

function topLine() {
  line(x1, y1, x2, y2);
}
function rightLine() {
  line(x2, y1, x2, y2 + height1 - i * 10);
}
function bottomLine() {
  line(x2 + 5, y1 + height1 - 10 * i, x1 + 5, y2 + height1 - 10 * i);
}
function leftLine() {
  line(x1 + 5, y1 + height1 - 10 * i, x1 + 5, y2);
}

function resetBuckets() {
  topBucket = 0;
  bottomBucket = 0;
  leftBucket = 0;
  rightBucket = 0;
}

function displayResults() {
  fill(255);
  textSize(24);

  text(topBucket, rectWidth / 2, height - topBucket);
  text(bottomBucket, rectWidth / 2 + rectWidth, height - bottomBucket );
  text(
    rightBucket,
    width - 2 * rectWidth + rectWidth / 2,
    height - rightBucket 
  );
  text(leftBucket, width - rectWidth / 2, height - leftBucket );
}

function persistentRects() {
  push();
  rr = map(topBucket, 0, topBucket, 0, 255);
  stroke(rr, 0, 0, 255);
  fill(rr, 0, 0, 255);
  rect(0, height - topBucket, rectWidth, topBucket);
  pop();

  push();
  bb = map(bottomBucket, 0, bottomBucket, 0, 255);
  stroke(0, 0, bb, 255);
  fill(0, 0, bb, 255);
  rect(rectWidth, height - bottomBucket , rectWidth, bottomBucket );
  pop();

  push();
  gg = map(rightBucket, 0, 100, 50, 255);
  stroke(0, gg, 0, 255);
  fill(0, gg, 00, 255);
  rect(2 * rectWidth, height - rightBucket , rectWidth, rightBucket );
  pop();

  push();
  noStroke();
  aa = map(leftBucket, 0, leftBucket * 2, 0, 255);
  stroke(255, 255, 255, 0);
  fill(aa, aa);
  rect(3 * rectWidth, height - leftBucket , rectWidth, leftBucket);
