function draw() {
  background(0);
  fill(255);
  noStroke();

  const data = fft.analyze();

  for (let i = 0; i < mount; i++) {
    const x = map(i, 0, 128, 0, 500);
    const y = map(data[i], 0, 255, 190, 10);
    // ellipse(x, y, 2, 2);
  }


  //   //////////////
  noStroke();
  ellipseMode(CENTER);
  translate(width / 2, height / 2);

  var noiseFrequency = .02;

  for (var i = 0; i < mount; i++) {


    offsetx = noise(10 * i + (millis() / 2500) * .2) * 1 * 220;
    offsety = noise(10 * i + 3 + (millis() / 2500) * .2) * 1 * 220;

    dots[i].x = i * 1.8 + offsetx - 80;
    dots[i].y = i * 2.5 + offsety - 190;


    dots[i].d = 5 + data[i] * 0.33;
    dots[i].e = data[i];

    dots[i].c = map(i, 0, mount, 70, 270);

    dots[i].r = noise(10 * i);
    dots[i].display();

  }
}

class DOT {
  contributor(x, y, d, c, r, e) {
    this.x = x;
    this.y = y;
    this.d = d;
    this.c = c;
    this.r = r;
    this.e = 255 - e;
  }
  display() {
    rotate(this.r);
    // fill(this.c, this.c-50, 200,100);

    // stroke(255);
    // strokeWeight(this.d/10);
    // ellipse(this.x, this.y, this.d);

    for (let i = 0; i < 10; i++) {
      // let trans = map(this.e, 0, 255, 255, 0);
      fill(this.c + 20, this.e);
      // stroke(0);
      // fill(255,-trans);
      ellipse(this.x, this.y, this.d + i * 1.2);

    }

  }
  move() {
    this.x++;
    this.y++;
  }
}



