const para = {};

const blocks = 30;

var colorPallete = ["f94144", "f3722c", "f8961e", "f9c74f", "90be6d", "43aa8b", "577590"];
var position = 0;

var points = [];
var blockSize;
var bgColor;

function setup() {
  createCanvas(windowWidth, windowHeight);
  background(255);
  shuffle(colorPallete, true);
  frameRate(blocks);

  initPattern();

  para.dir = 2;
  pane.addInput(para, "dir", {min:0, max:10});
  para.time = -40;
  pane.addInput(para, "time", {min:-50, max:0});

}

function draw() {
  generatePattern();
  pane.refresh();
