var cat;
var ground;
var light;
//the scene is half the size of the canvas
var windowWidth, windowHeight;
var SCENE_W = windowWidth/2;
var SCENE_H = windowHeight/2;

function setup() {
  createCanvas(windowWidth, windowHeight);
  frameRate(30);

  light = loadImage('Lantern Light.png');
  cat = createSprite(400, 200, 100, 100);

  cat.addAnimation('walking', 'cat_walk_2.1.png', 'cat_walk_2.2.png', 'cat_walk_2.3.png', 'cat_walk_2.4.png', 'cat_walk_2.5.png', 'cat_walk_2.6.png');

    ground = new Group();

    //create some background for visual reference
  for(var i=0; i<80; i++)
  {
    //create a sprite and add the 3 animations
    var light_shadow = createSprite(random(-width, SCENE_W+width), random(-height, SCENE_H+height));
    //cycles through light_shadows 0 1 2
    light_shadow.addAnimation('normal', 'Lantern Light'+i%3+'.png');
    ground.add(light_shadow);
  }
}

function draw() {
  background(0);

  //draw the scene
  //light_shadows first
  drawSprites(ground);
  image(light, 100, 100);

  drawSprite(cat);

  noStroke();
  fill(254, 252, 0, 255);
  ellipse(cat.position.x, cat.position.y-200, 30, 30);

  //mouse trailer, the speed is inversely proportional to the mouse distance
  cat.velocity.x = (camera.mouseX-cat.position.x)/10;
  cat.velocity.y = (camera.mouseY-cat.position.y)/10;

  //1.5 zoom is zooming in (150% of the normal size)
  if(mouseIsPressed){
    camera.zoom = 1.5;
    frameRate(120);
    noStroke();
    fill(255, 0, 0, 255);
    ellipse(cat.position.x, cat.position.y-200, 100, 100);
  }else{
    camera.zoom = 1;
    frameRate(30);
    noStroke();
    fill(254, 252, 0, 255);
    ellipse(cat.position.x, cat.position.y-200, 30, 30);
    }
  //a camera is created automatically at the beginning

  //set the camera position to the cat's position
  camera.position.x = cat.position.x;
  camera.position.y = cat.position.y;

  //limit the cat movements
  if(cat.position.x < 0)
    cat.position.x = 0;
  if(cat.position.y < 0)
    cat.position.y = 0;
  if(cat.position.x > SCENE_W)
    cat.position.x = SCENE_W;
  if(cat.position.y > SCENE_H)
    cat.position.y = SCENE_H;

  camera.off();
}

