const dim = {
  x: 800,
  y: 800
};

const fr = 8;

var segments = 2;
const segmentsMax = 15;

var h = 0;

function setup() {
  createCanvas(dim.x, dim.y);

  noFill();
  noStroke();
  colorMode(HSB, 100);

  frameRate(fr);
}

function draw() {
  background(0, 0, 100);

  let block = {
    w: width / segments,
    h: height / segments
  };

  for (let i = 0; i < segments; i++) {
    for (let j = 0; j <= i; j++) {

      let triangleBox = {
        w: width - block.w * i,
        h: height - block.h * i
      };

      let centroid = {
        x: width / 2 - (block.w / 2) * i + block.w * j,
        y: height / 2 + (block.h / 2) * i
      };

      fill(h, 100, 40, 5);

      triangle(
        centroid.x, centroid.y - triangleBox.h / 2,
        centroid.x - triangleBox.w / 2, centroid.y + triangleBox.h / 2,
        centroid.x + triangleBox.w / 2, centroid.y + triangleBox.h / 2
      );
    }
  }

  h += 0.5;
  h = h % 100;
  segments += 0.2;

  if (segments > segmentsMax)
    segments = segmentsMax;
}

