function setup() {
  createCanvas(dim, dim);
  background(255);

  colorPallete[0] = shuffle(colorPallete[0]);
  colorPallete[1] = shuffle(colorPallete[1]);

  generateCanvas();

}

function draw() {

}

function generateCanvas() {

  let grid = int(random(5, 10));
  let blockSize = int(dim / grid);

  let limit = dim - ((blockSize + margin) * (grid-1)) - margin;
  translate(limit/2, limit/2);

  for (let i = margin; i < dim; i += blockSize + margin) {
    for (let j = margin; j < dim; j += blockSize + margin) {
      if(i+blockSize > dim || j+blockSize > dim)
        break;
      generateSquare(i, j, blockSize);
    }
  }
}

function generateSquare(x, y, size) {

  noStroke();
  fill(getColor(1));
  rect(x, y, size, size);

  for (let i = 0; i < int(random(1, 10)); i++) {
    let pointRadius = size / 10;
    stroke(getColor(0));
    strokeWeight(pointRadius);
    point(random(x + 2 * pointRadius, x + size - 2 * pointRadius), random(y + 2 * pointRadius, y + size - 2 * pointRadius));
  }

  strokeWeight(4);
}

