  // i = 0 --> POINTS
  // i = 1 --> SQUARES

  // find the value at the current position in the deck
  var v = colorPallete[i][position[i]];

  // change the position for next time
  position[i]++;

  // if we run out of "cards", shuffle and start over from the top
  if (position[i] == colorPallete[i].length) {
    colorPallete[i] = shuffle(colorPallete[i]);
    position[i] = 0;
  }

  // return the value
  return str("#" + v);
}

