function drawBranch(length) {

  if (length < 2) {
      for (let i=0;i<190;i++){
          myTurtle.turnTo(i*i*sin(i*3))
          myTurtle.moveForward(1)
        }
      return;
  }

  // draw this branch
  stroke(250,220,20)
  strokeWeight(length / 120);
  myTurtle.moveForward(length * .05);
  myTurtle.turnLeft(9);
  myTurtle.turnRight(15);
  myTurtle.moveForward(length * .85);

  // left child
  myTurtle.pushState();
  myTurtle.turnLeft(random(20, 40));
  drawBranch(length * 0.10);

  myTurtle.popState();

  // right child
  myTurtle.pushState();
  myTurtle.turnRight(random(1 ,12));
  drawBranch(length * 0.75);
  myTurtle.popState();
drawpetal1();

