var numCircles = 100;

function setup() {
  createCanvas(600, 600);
  frameRate(30);
}

function draw() {
  background(140, 140,0);
  translate(width/2, height/2);
  noFill();
  for (let j = 0; j < numCircles; j++){
    let inc = noise(0.1, 0.6);
    let r = j;
    let lowMap = 20 + map(noise(j/2, frameCount), 0, 1, 50, 100);
    let highMap = j * 10 + 40
  drawJaggedCircle(r, lowMap, highMap, inc);
}
}

function drawJaggedCircle(r, lowMap, highMap, inc){
  beginShape();
  stroke(random(100, 255), 255, 255)
  for (let a = 0; a < TWO_PI; a+= inc){
    r = map(noise(r, a), 0, 1, lowMap, highMap)
    let x = r * cos(a*frameCount/10);
    let y = r * sin(a*frameCount/10);
    vertex(x,y);
  }
  endShape(CLOSE);
}

