// reference: http://paperjs.org/examples/radial-rainbows/
var point = view.center;

var colors = [];
var cycles = 10;
for (var i = 0, l = 60; i < l; i++) {
	var brightness = 1 - (i / l) * 1.5;
// var brightness = i / l * cycles * 10;
// var brightness = 6*i/l;
	var hue = i / l * cycles * 100;
	var color = {
		hue: hue,
		saturation: 10,
		brightness: brightness
	};
	colors.push(color);
}

var radius = Math.max(view.size.width, view.size.height) * 0.75;

var path = new Path.Rectangle({
	rectangle: view.bounds,
	fillColor: {
		origin: point,
		destination: point + [radius, 0],
		gradient: {
			stops: colors,
			radial: true
		}
	}
});

var color = path.fillColor;
var gradient = color.gradient;
var mouseDown = false;
var mousePoint = view.center;

function onMouseDown(event) {
	mouseDown = true;
	mousePoint = event.point;
}

function onMouseDrag(event) {
	mousePoint = event.point;
}

function onMouseUp(event) {
	vector.length = 10;
	mouseDown = false;
}

var grow = false;
var vector = new Point(150, 0);

function onFrame() {
	for (var i = 0, l = gradient.stops.length; i < l; i++)
		gradient.stops[i].color.brightness -= 0.01;
	if (grow && vector.length > 300) {
		grow = false;
	} else if (!grow && vector.length < 75) {
		grow = true;
	}
	if (mouseDown) {
		point = point + (mousePoint - point) / 10;
	} else {
		vector.length += (grow ? 1 : -1);
		vector.angle += 1;
	}
	color.highlight = mouseDown ? point : point + vector;
}

function onResize(event) {
	point = view.center;
	path.bounds = view.bounds;
	var color = path.fillColor;
	color.origin = point;
	var radius = Math.max(view.size.width, view.size.height) * 0.75;
	color.destination = point + [radius, 0];
}

