let spherePoints = {};
let noteColors = {
  A2: [255],
  G2: [225],
  B2: [225],
  E2: [225],
  D2: [225],
  C2: [225],
};

function setup() {
  createCanvas(800, 800, WEBGL);
background(0);

  toneLoop = new Tone.Loop(function(time) {
    let noteName = chain.next();

    let notes = Object.keys(transitions);
    notes.map(note => {
      spherePoints[note].lightOn = note === noteName;
    });

  }, "8n").start(0);

  Tone.Transport.start();

  createDirectionalLights();
   ambientLight(40);
}

function draw() {
  osc.frequency.value = chain.value;
  let locX = mouseX - windowHeight / 2;
  let locY = mouseY - windowWidth / 2;

  pointLight(255, 255, 255, locX, locY, 200);
  pointLight(255, 255, 255, locX + 50, locY + 50, 200);
  pointLight(255, 255, 255, locX - 50, locY - 50, 200);

  Object.values(spherePoints).map(point => {
    push();
    translate(point.x-50, point.y-100);
    let directionLightColor = point.lightOn ? [255, 255, 255] : [0, 0, 0];
    directionalLight(...directionLightColor, point.x + 10, point.y + 10, -10);

    rotateZ(point.rotation);
    rotateX(-50);

    specularMaterial(...point.color);
    ellipse(1, 1,100,100);
    pop();
  })
}

function createDirectionalLights() {
  let notes = Object.keys(transitions);
  let angleIncrement = TWO_PI / notes.length;
  let r = (windowWidth / 2) - 150;

  for (let i = 0; i < notes.length; i += 1) {
    let angle = i * angleIncrement;
    let note = notes[i];

    let x = r * sin(angle);
    let y = r * cos(angle);

    spherePoints[note] = {
      x,
      y,
      rotation: -angle,
      color: noteColors[note],
      lightOn: false,
    };
  }
}

