    noLoop();
}

function getQuick(img, x, y) {
    var i = (y * img.width + x) * 4;
    return [
      img.pixels[i],
      img.pixels[i + 1],
      img.pixels[i + 2],
      img.pixels[i + 3]
    ];
  }

