function generateNumber() {
  // even distribution
  for (let i = 0; i < 80; i++) {
    displayImages.push(random(0, 10));
  }
  console.log(displayImages);
}

function draw() {
  finalDisplay.splice(0, finalDisplay.length);
  clear();
  background(0);
  // finalDisplay = [];
  // let val = sliderMean.value();
  // let val2 = sliderSD.value();

  let val = map(noise(sliderMean.value() + millis() * 0.005), 0, 1, 0, 10);
  let val2 = sliderSD.value();
  // var list = createListOfNNumbersBetweenAAndB(9, 1, 10);
  newList = transfomListToExactMeanAndSd(displayImages, val, val2);
  console.log(newList);
  console.log(
    "transformed list, mean and sd",
    newList,
    computeMeanSdAndItervalRangeMinMax(newList)
  );
  console.log(
    "original list, mean and sd",
    displayImages,
    computeMeanSdAndItervalRangeMinMax(displayImages)
  );

  //////////
  function computeMeanSdAndItervalRangeMinMax(Ratings) {
    const sum = Ratings.reduce((a, b) => a * 1 + b * 1, 0);
    const mean = sum / Ratings.length;
    const sumMinusMean = Ratings.reduce(
      (a, b) => a + (b - mean) * (b - mean),
      0
    );
    //console.log(sum);
    return {
      mean: mean,
      sd: Math.sqrt(sumMinusMean / (Ratings.length - 1)),
      range: [Math.min(...Ratings), Math.max(...Ratings)],
    };
  }
  /////
  function transfomListToExactMeanAndSd(Ratings, mean, sd) {
    const current = computeMeanSdAndItervalRangeMinMax(Ratings);
    return Ratings.map((n) =>
      ((sd * (n - current.mean)) / current.sd + mean).toFixed(3)
    );
  } // 1 * (9 - RatingsMean) / Ratings SD + mean

  //   goal = newList[0];

  newList.forEach((finalData) => {
    var closest = Ratings.reduce(function (prev, curr) {
      return Math.abs(curr - finalData) < Math.abs(prev - finalData)
        ? curr
        : prev;
    });
    finalDisplay.push(Ratings.indexOf(closest));
  });
  for (let i = 0; i < newList.length; i += 1) {
    if (newList[i] * 10 < 100) {
      fill("blue");
    } else {
      fill("red");
    }
    ellipse(i * 10, newList[i] * 10, 10);
    line(i * 10, newList[i] * 10, i * 10 + 10, newList[i + 1] * 10);
  }

  //console.log(finalDisplay);
  let newMap = [];
  let newMap2 = [];
  for (let i = 0; i < newList.length; i++) {
    newMap[i] = map(newList[i], 0, max(newList), 0, height);
    newMap2[i] = map(finalDisplay[i], 0, max(finalDisplay), 0, height);
  }
  //console.log(newMap);

  for (let i = 0; i < finalDisplay.length; i += 1) {
    if (finalDisplay[i] * 10 < 100) {
      fill("yellow");
    } else {
      fill("pink");
    }
    ellipse(i * 10, finalDisplay[i] * 10, 5);
    stroke(255, 200, 100);
    line(i * 10, newMap[i], i * 10 + 10, newMap[i + 1]);
    ellipse(i * 10, newMap[i], 5);

    if (newMap2[i] > height / 2) {
      fill(0);
    } else {
      fill(255);
    }
    ellipse(i * 10, newMap2[i], 5);
    stroke(255, 0, 100);
    line(i * 10, newMap2[i], width, newMap2[i]);
    if (
      newMap2[i] > height / 8 &&
      newMap2[i] < height / 2 &&
      newMap2[i + 1] < height / 2 &&
      newMap2[i + 1] > height / 8
    ) {
      stroke(255);
      line(i * 10, newMap2[i], i * 10 + 10, newMap2[i + 1]);
    }

    stroke(0);
    line(i * 10, newMap2[i], i * 10 + 10, newMap2[i + 1]);

    stroke(255);
    line(i * 10, finalDisplay[i] * 10, i * 10 + 10, finalDisplay[i + 1] * 10);
  }
}

