  var centerSunX = getRandom(marginLeft, view.size.width) - marginRight;

  var centerSunY = getRandom(0, 20);
  var sun = new Path.Circle({
    center: new Point(centerSunX, centerSunY),
    radius: 20,
  });
  sun.fillColor = {
    gradient: {
      stops: ["#f57d05", "#f57d05", "#f5b505", "#f5b505", "#f57d05"],
      radial: false,
    },
    origin: sun.bounds.leftCenter,
    destination: sun.bounds.rightCenter,
  };
  sun.shadowColor = new Color(255, 255, 255);
  sun.shadowBlur = 15;
  sun.shadowOffset = new Point(5, 5);

  sun.rotate(getRandom(0, 360));

  var sunRay = Path.Circle({
    center: new Point(centerSunX, centerSunY),
    radius: 60,
  });

  sunRay.fillColor = {
    gradient: {
      stops: ["#f57d05", "#f57d05", "#f5b505", "#f5b505", "#f57d05"],
      radial: false,
      opacity: 0.5,
    },

    origin: sunRay.bounds.leftCenter,
    destination: sunRay.bounds.rightCenter,
  };
  sunRay.opacity = 0.1;

  sunRay.flatten(2);
}

