let waves=[]

class Wave{
  constructor(color,h){
    this.waveColor=color
    this.waveHeight=h
    this.offset=random(100,200)
    this.t=0

  }
  display(){
    let xoff=0
    fill(this.waveColor)
    beginShape()
    for(let x=0;x<=width+15;x+=15){
      let yoff=map(noise(xoff+this.offset,this.t+this.offset),0,1,0,200)
      let y = this.waveHeight-yoff
      vertex(x,y)
      xoff+=0.08

      }
    vertex(width+100,height)
    vertex(0,height)
    endShape(CLOSE)
  }
  update(){
    this.t+=0.05
  }
}
