function bounce(){
    for (var i = 0; i < allSprites.length; i++) {
    var spri = allSprites[i];
    if (spri.position.x < 0) {
      spri.position.x = .5;
      spri.velocity.x = 5;
    }

    if (spri.position.x > 800) {
      spri.position.x = 799;
    spri.velocity.x = -5;
    }

    if (spri.position.y < 0) {
        spri.position.y = .5;
       spri.velocity.y = 5;
    }

    if (spri.position.y > 800) {
      spri.position.y = 799;
      spri.velocity.y = -abs(spri.velocity.y);
    }
  }
}


function keyPressed() {
  if (keyCode == RIGHT_ARROW) {
    sprite2.setSpeed(2, 0);
  }
  else if (keyCode == DOWN_ARROW) {
    sprite2.setSpeed(2, 90);
  }
  else if (keyCode == LEFT_ARROW) {
    sprite2.setSpeed(2, 180);
  }
  else if (keyCode == UP_ARROW) {
    sprite2.setSpeed(2, 270);
  }
  else if (key == ' ') {
    sprite2.addSpeed(0, 0);
  }
  return false;
}

