// Based on the noise wave example from Daniel Shiffman in the p5 examples: https://p5js.org/examples/math-noise-wave.html

canvasSize = 800;
let yoff = 0.0; // 2nd dimension of perlin noise

function setup() {
  createCanvas(canvasSize, canvasSize);
  ellipseMode(CENTER);
  fill(200, 50, 100);
  noStroke();
  let n;
}

function draw() {
  background(184, 208, 235);
  noStroke();
  fill(25, 130, 196, 255);
  // We are going to draw a polygon out of the wave points
  beginShape();

  let xoff = 0; // Option #1: 2D Noise

  // Iterate over horizontal pixels
  for (let x = 0; x <= width; x += 10) {
    // Calculate a y value according to noise
    let y = map(noise(xoff, yoff), 0, .25, 200, 300);

    // Set the vertex
    vertex(x, y);
    // Increment x dimension for noise
    xoff += 0.05;
  }
  // increment y dimension for noise
  yoff += 0.01;
  vertex(width, height);
  vertex(0, height);
  endShape(CLOSE);
  }

