// require https://cdn.jsdelivr.net/npm/p5@1.2.0/lib/p5.js
const dim = 800;
const size = 25;

function setup() {
  createCanvas(dim, dim);
  background('black');
  generateCanvas();
}

function draw() {

}

function generateCanvas() {
  strokeWeight(6);
  stroke('white');

  let blockSize = dim / size;

  for (let i = 0; i < size; i++) {
    for (let j = 0; j < size; j++) {
      let x = i * blockSize;
      let y = j * blockSize;
      switch (floor(random(0, 4))) {
        case 0: // element - '\'
          line(x, y, x + blockSize, y + blockSize);
          break;
        case 1: // element - '/'
          line(x + blockSize, y + blockSize, x, y + blockSize);
          break;
        case 2: // element - '-'
          line(x, y + blockSize / 2, x + blockSize, y + blockSize / 2);
          break;
        case 3: // element - '|'
          line(x + blockSize / 2, y, x + blockSize / 2, y + blockSize);
          break;
      }
    }
  }
}

