	createCanvas(1000, 1000);
	myTurtle = new Turtle();
	colorMode(HSL, 100);
}

function draw() {
	background(200);

	strokeWeight(1);

	// draw the triangles
    for (let j = 0; j < numPomPoms; j++){
        drawPomPom(floor(random(100, 1000)), floor(random(100,1000)), random(100, 200));
    }


	noLoop();
}

function drawPomPom(x, y, size){
        // move to starting position (without drawing)
    	myTurtle.penUp();
    	myTurtle.moveTo(x, y);

    	// put the pen down to draw
    	myTurtle.penDown();

    	let h = floor(random(100));

    	for (var k = 0; k < size/3; k++){
	   	myTurtle.pushState();
	   	myTurtle.turnLeft(25*k);

    	for (var i = 0; i < size; i++) {
    	    stroke(h, 80, random(80, 100));
    	    myTurtle.pushState();
    	    myTurtle.moveForward(1 + i*1.5);
    	    myTurtle.popState();
    	    myTurtle.turnLeft(3);
    	    myTurtle.penUp();
    	    myTurtle.moveTo(x-((i*1.5)/2), y + 10);
    	    myTurtle.penDown();
    	} 
	}
}

