	const y1 = 120 - offsetA;

	push();

	ambientLight(0, 0, 255);

	pointLight(0, 0, 255, -200, 0, 250);
	pointLight(255, 0, 0, 200, 0, 250);

	translate(-100, y1);

	rotateX(angle);
	rotateY(angle * 0.3);
	rotateZ(angle * 1.2);

	noStroke();
	ambientMaterial(255);

	torus(40, 30);

	angle += 0.07;

	pop();

	///// RING 2


	const b = map(frameCount, 0, 20, 0, PI);
	let offsetB = sin(a * 1.2) * 100;
	offsetB = abs(offsetB);
	const y2 = 120 - offsetB;

	push();

	ambientLight(0, 255, 0);

	pointLight(0, 0, 255, -200, 0, 250);
	pointLight(255, 0, 0, 200, 0, 250);

	translate(0, y2);

	rotateX(angle);
	rotateY(angle * 0.3);
	rotateZ(angle * 1.2);

	noStroke();
	ambientMaterial(255);

	torus(50, 30);

	angle += 0.07;

	pop();

	///// RING 3

	const c = map(frameCount, 0, 20, 0, PI);
	let offsetC = sin(a * .8) * 300;
