function drawBranch(length) {

    if (length < 1) {
        return;
    }

    strokeWeight(length / 9);
    myTurtle.moveForward(length * .6);
    myTurtle.turnLeft(10);
    myTurtle.moveForward(length * .5);

    myTurtle.pushState();
    myTurtle.turnLeft(50);
    drawBranch(length * 0.75);

    myTurtle.popState();

    myTurtle.pushState();
    myTurtle.turnRight(random(10, 30));
    drawBranch(length * 0.75);
    myTurtle.popState();

}

function drawBranch2(length) {

    if (length < 1) {
        return;
    }

    strokeWeight(length / 9);
    myTurtle.moveBackward(length * .6);
    myTurtle.turnRight(10);
    myTurtle.moveBackward(length * .5);

    myTurtle.pushState();
    myTurtle.turnRight(50);
    drawBranch2(length * 0.75);

    myTurtle.popState();

    myTurtle.pushState();
    myTurtle.turnLeft(random(10, 30));
    drawBranch2(length * 0.75);
    myTurtle.popState();

}

function drawBranch3(length) {

    if (length < 1) {
        return;
    }

    strokeWeight(length / 9);
    myTurtle.moveBackward(length * .6);
    myTurtle.turnLeft(10);
    myTurtle.moveBackward(length * .5);

    myTurtle.pushState();
