    myTurtle.popState();
}

function drawBranch4(length) {

    if (length < 1) {
        return;
    }

    strokeWeight(length / 9);
    myTurtle.moveForward(length * .6);
    myTurtle.turnRight(10);
    myTurtle.moveForward(length * .5);

    myTurtle.pushState();
