var t=0;
let T=true;
var d=0;


function setup() {
    createCanvas(400, 400);

    createP('Time speed');
    timeSpeed_slider = createSlider(0, 100, 50);
}


function draw() {
    background(10);
    var ts =timeSpeed_slider.value()/500;

    var noiseFrequency = .02;
    const points=[];
    // points.push({
    //     x: 200,
    //     y: 200
    //   });
    var i=0;
    for(var i=0;i<30;i++) {
        // const p =point[i];
        noStroke();
        ellipseMode(CENTER);
        var x = noise(i,2)*400;
        var y = noise(i,1)*400;
        // var x= random(200)+random(200);
        // var y= random(200)+random(200);

        // the diameter shouldn't always be 10, it needs to vary
        var diameter = noise(x*noiseFrequency,y*noiseFrequency)*20;

        d=0;
        T=true;
        var j=0;
        while(j<points.length){
            d = dist(points[j].x,points[j].y,x,y);
            if(d<(points[j].r*2+diameter)){
                x=x+noise(i,3)*5;
                y=y+noise(i,4)*5;
                j=0;
            }else(j++)

        }


        points.push({
                x: x,
                y: y,
                r: diameter
              });

        // the colors also need to change
        // what colorMode would be easiest to work with?
        colorMode(HSB);
        var h= map(points[i].r,3,17,80,360);
        fill(h,100,100);

        ellipse(points[i].x, points[i].y, points[i].r, points[i].r);

        radius=points[i].r*2;
        s=100;
        for(var j=0; j<(points[i].r/2);j++){
            noFill();
            stroke(h,s,100);
            ellipse(points[i].x,points[i].y,radius,radius);
            radius = radius+noise(j+t,i)*10;
            s=s-noise(j,i)*15;
        }

        }


    t+=ts;


    // noLoop();


}

