function treaMap(){
    let yoff=0;
    let j=0;
    let treaX=0;
    let treaY=0;
    loadPixels();
    for (let x=0;x<width; x++){
        let xoff=0;
        for (let y=0;y<height; y++){
            let index =(x + y * width)*4;
            let r= floor(noise(xoff,yoff)*400);

            let r1=map(floor(r/17),0,20,0,255);
            // r2 = r/20;
            // console.log(r,r1);
            if(r%17 <= 1){
            pixels[index]=80;
            pixels[index+1]=60;
            pixels[index+2]=30;
            pixels[index+3]=255;
            }else{
            pixels[index]=50;
            pixels[index+1]=r1-10;
            pixels[index+2]=290-r1;
            pixels[index+3]=255;

            }
            if (r1>=j&&x>40){
                j=r1;
                treaX=x;
                treaY=y;
            }
            // console.log(xoff);
            xoff+=.01;
        }
        yoff+=.01;
    }
    updatePixels();

    stroke(189, 0,72);
    strokeWeight(4);
    line(treaX-5,treaY-5,treaX+5,treaY+5);
    line(treaX-5,treaY+5,treaX+5,treaY-5);
    // line(treaX-5,treaX-5,treaY+5,treaY+5);
}

