    var newDistance = dist(x, y, mouseX, mouseY);

    textSize(minTextSize + newDistance * 2 / 3);

    var letter = newString.charAt(i);
    var step = textWidth(letter);

    if (newDistance > step) {
      var theta = atan2(mouseY - y, mouseX - x);
      push();
      translate(x, y);
      rotate(theta + random(delta)*frameCount*0.0012);
      text(letter, 0, 0);
