function setup() {
  createCanvas(400, 400);
  colorMode(RGB, 255);
  noStroke();
}

function draw() {
  background(1, 3, 38);
  let j = map(noise(frameCount/150), 0, 1, 10, 40);
  let x1 = 0;
  let y1 = 0;
  let x2 = j;
  let y2 = 0;
  let x3 = 0;
  let y3 = j;
  for (let i = 0; i < height; i += j){
    for (let k = 0; k < width; k ++){
      let q = noise(k, i, frameCount/150);
      if (q < 1/4){
        fill(140, 17, 111);
      } else if (q < 2/4){
        fill(1, 3, 38);
      } else if (q < 3/4) {
        fill(192, 217, 4);
      } else {
        fill(217, 15, 70)
      }
      triangle(x1, y1, x2, y2, x3, y3);
      x3 += j;
      let b = noise (i, k, frameCount/150)
      if (b < 1/4){
        fill(192, 217, 4);
      } else if (b < 2/4){
        fill(217, 15, 70);
      } else if (b < 3/4) {
        fill(1, 3, 38);
      } else {
        fill(140, 17, 111);
      }
      triangle(x2, y2, x3, y3, x1, y3)
      x1 += j;
      x2 += j;

    }
    x1 = 0;
    x2 = j;
    x3 = 0;
    y1 += j;
    y2 += j;
    y3 += j;
  }
  noLoop();
}

